                top: 3px;
                width: 4px;
                height: 9px;
                border: solid white;
                border-width: 0 1.8px 1.8px 0;
                transform: rotate(45deg);
            }

            a {
                color: #999999;
                font-style: normal;
                font-weight: normal;
                font-size: 16px;
                font-family: "SF Pro Text", sans-serif;
            }

            a:hover {
                color: #c9c9c9;
            }
        }
    }

    .btn-upgrade {
        flex-shrink: 0;
    }

    .btn-upgrade a {
        color: #ffffff;
        background: #2CBF67;
        font-size: 15px;
        font-weight: normal;
        font-style: normal;
        font-family: Lato, sans-serif;
        padding: 15px 28px;
        border-radius: 3px;
        text-decoration: none;
        transition: all .1s linear;
    }

    .btn-upgrade a:hover {
        background: #17a951;
    }

    .close-banner {
        position: absolute;
        top: 10px;
        right: 5px;
        border: 0;
        background: transparent;
        text-decoration: none;

        span {
            font-size: 15px;
            display: flex;
            align-items: center;
            justify-content: center;
            color: #404040;
            transition: all .2s ease;
            cursor: pointer;
            border: 1px solid #3f3f3f;
            border-radius: 55px;
            width: 20px;
            height: 20px;
        }

        span:hover {
            color: #f16982;
            border-color: #f16982;
        }
    }
}

